for (i in seq_along(directions)) {
  if (directions[i] == "R") {
    arrow <- rev(arrow) * c(1, -1)
  } else if (directions[i] == "L") {
    arrow <- rev(arrow * c(1, -1))
  }

  for (step in seq_len(value[i])) {
    position <- rbind(position, arrow + last(position))
